updatePreview.code:
function updatePreview() {
    const title = document.getElementById('blockTitle').value || 'New Time Block';
    const date = document.getElementById('blockDate').value;
    const startTime = document.getElementById('blockStartTime').value;

    let totalMinutes = getDurationInMinutes();
    const start = new Date(`${date}T${startTime}`);
    const end = new Date(start.getTime() + totalMinutes * 60000);

    updatePreviewDisplay(title, start, end, totalMinutes);
}

getDurationInMinutes.code:
function getDurationInMinutes() {
    const preset = document.getElementById('durationPreset').value;

    if (preset === 'custom') {
        const custom = document.getElementById('customDuration').value;
        if (custom) {
            const [hours, minutes] = custom.split(':').map(Number);
            return hours * 60 + minutes;
        }
    }

    return parseInt(preset) || 120;
}

validateCustomDuration.code:
function validateCustomDuration(value) {
    const pattern = /^[0-9]:[0-5][0-9]$/;
    return pattern.test(value);
}

createTimeBlock.code:
function createTimeBlock() {
    const blockData = collectFormData();

    if (validateBlockData(blockData)) {
        saveTimeBlock(blockData)
            .then(() => {
                showSuccessMessage('Time block created successfully!');
                closeModal();
                refreshCalendarView();
            })
            .catch(error => {
                showErrorMessage('Failed to create time block. Please try again.');
            });
    }
}

selectIcon.code:
function selectIcon(iconElement) {
    document.querySelectorAll('.icon-option').forEach(opt => {
        opt.classList.remove('selected');
    });

    iconElement.classList.add('selected');
    selectedIcon = iconElement.dataset.icon;

    document.querySelector('.icon-display').textContent = selectedIcon;
    updatePreview();
}

selectColor.code:
function selectColor(colorElement) {
    document.querySelectorAll('.color-option').forEach(opt => {
        opt.classList.remove('selected');
    });

    colorElement.classList.add('selected');
    selectedColors = colorElement.dataset.colors.split(',');

    updateIconDisplayBackground();
    updatePreview();
}

handleDurationPresetChange.code:
function handleDurationPresetChange(selectElement) {
    const customInput = document.getElementById('customDuration');

    if (selectElement.value === 'custom') {
        customInput.style.display = 'block';
        customInput.focus();
    } else {
        customInput.style.display = 'none';
        updatePreview();
    }
}

handleCustomDurationInput.code:
function handleCustomDurationInput(inputElement) {
    const value = inputElement.value;

    if (validateCustomDuration(value) || value === '') {
        updatePreview();
    } else {
        // Show validation error or prevent invalid input
        inputElement.classList.add('error');
    }
}

formatTime.code:
function formatTime(date) {
    return date.toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
    });
}

formatDate.code:
function formatDate(dateString) {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric'
    });
}

formatDuration.code:
function formatDuration(minutes) {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;

    if (hours > 0 && mins > 0) {
        return `${hours}h ${mins}m`;
    } else if (hours > 0) {
        return `${hours}h`;
    } else {
        return `${mins}m`;
    }
}

