function collectFormData() {
    return {
        title: document.getElementById('blockTitle').value,
        description: document.getElementById('blockDescription').value,
        date: document.getElementById('blockDate').value,
        startTime: document.getElementById('blockStartTime').value,
        duration: getDurationInMinutes(),
        icon: selectedIcon,
        colors: selectedColors,
        type: selectedBlockType,
        linkedItem: document.getElementById('linkToItem').value,
        reminder: document.getElementById('reminderTime').value
    };
}

