def get_linkable_items(user_id, item_types=['goals', 'tasks', 'habits']):
    result = {}

    if 'goals' in item_types:
        result['goals'] = Goal.query.filter_by(
            user_id=user_id, 
            status='active'
        ).all()

    if 'tasks' in item_types:
        result['tasks'] = Task.query.filter_by(
            user_id=user_id, 
            status='pending'
        ).limit(20).all()

    if 'habits' in item_types:
        result['habits'] = Habit.query.filter_by(
            user_id=user_id, 
            status='active'
        ).all()

    return result
